export class Fehler_box{constructor(){this._html=this._html_generieren()}_html_generieren(){let e=document.createElement("div");return e.className="fehlerbox",e.innerHTML="<span>Es gibt Fehler in folgenden Eingabefeldern</span>\n         <ul>\n         </ul>",e}anzeigen(e){this._entfernen(),e.forEach(e=>{let n=document.createElement("li");n.textContent=e,this._html.querySelector("ul").insertAdjacentElement("beforeend",n),document.querySelector("#eingabeformular").insertAdjacentElement("beforebegin",this._html)})}_entfernen(){document.querySelectorAll(".fehlerbox").forEach(e=>e.remove())}}